import React from 'react';
import { withStyles } from '@material-ui/core/styles';
import List from '@material-ui/core/List';
import Button from "components/CustomButtons/Button.jsx";

//css file
import tablePageStyle from "assets/jss/material-kit-react/views/tablePage.jsx";

class TableStats extends React.Component {

  // state = {
  //   list: [
  //     {
  //       title: 'Tyre Model',
  //       description: ' BCVHGU1278391734',
  //       type: 'text'
  //     },
  //     {
  //       title: 'Carbon Used',
  //       description: '  Premium Quality',
  //       type: 'text'
  //     },
  //     {
  //       title: 'Tire Size : ',
  //       description: ' 20 inches',
  //       type: 'text'
  //     },
  //     {
  //       title: 'Axis Size : ',
  //       description: '13 Inches',
  //       suitable: 'success',
  //       type: 'compare',
  //     },
  //     {
  //       title: 'Axis Alignment : ',
  //       description: '2 Inches',
  //       suitable: 'danger',
  //       type: 'compare'
  //     }
  //   ]
  // }


  render() {
    const { classes} = this.props;

    return (
      <List className={classes.root} subheader={<li />}>
        {[0, 1].map(sectionId => (
          <li key={`section-${sectionId}`} className={classes.listSection}>
            <ul className={classes.ul}>

              {
                this.props.list.map((el, index) => {

                  if (el.type === 'text') {
                    return (
                      <div className={classes.root} >
                        <h4 className={classes.title}>{el.title} :  </h4>
                        <h5 style={{ display: "inline", paddingLeft: '10px' }}>{el.description}</h5>
                      </div>
                    );
                  } else if (el.type === 'compare') {
                    return(
                      <div className={classes.typo} >
                      <h4 className={classes.title}>{el.title} </h4>
                      <Button style={{ display: "inline", marginLeft: '10px' }} color={ el.suitable}>{el.description}</Button>
                    </div>
                    );                  
                  }
                })
              }

            </ul>
          </li>
        ))}
      </List>
    );
  }
}







export default withStyles(tablePageStyle)(TableStats);
