import React from 'react';

import { withStyles } from '@material-ui/core/styles';
import List from '@material-ui/core/List';
import classNames from "classnames";
import GridItem from "components/Grid/GridItem.jsx";
import Button from "components/CustomButtons/Button.jsx";
import CustomInput from "components/CustomInput/CustomInput.jsx";
import CustomLinearProgress from "components/CustomLinearProgress/CustomLinearProgress.jsx";
import Success from "components/Typography/Success.jsx";
//css file
import tablePageStyle from "assets/jss/material-kit-react/views/tablePage.jsx";

class TableStats extends React.Component {

  state = {
    list : [
      { title : 'Tyre Model' ,
        description : ' BCVHGU1278391734'
      } ,
      {
        title : 'Carbon Used' ,
        description : ' BCVHGU1278391734'
      } ,
      {
        title : 'Tire Size : ' ,
        description : ' BCVHGU1278391734'
      } ,
      {
        title : 'Axis Size : ' ,
        description : '13 Inches' ,
        suitable : 'Yes'
      } ,
      {
        title : 'Axis Alignment : ' ,
        description : '2 Inches' ,
        suitable : 'No'
      }     
    ]
  }

  componentDidMount() {
    const arrOfInfo = [...this.state.list];
    // console.log(arrOfInfo[0].title);
  }

  render() {
    const { classes, ...rest } = this.props;

    // const imageClasses = classNames(
    //   classes.imgRaised,
    //   classes.imgRoundedCircle,
    //   classes.imgFluid
    // );
    // const navImageClasses = classNames(classes.imgRounded, classes.imgGallery);
    // console.log({arrOfInfo});
    return (
      <List className={classes.root} subheader={<li />}>
        {[0, 1].map(sectionId => (
          <li key={`section-${sectionId}`} className={classes.listSection}>
            <ul className={classes.ul}>

              {console.log(this.state.list) }

              <div className={classes.root} >
                <h4 className={classes.title}>Tyre Model :  </h4>
                <h5 style={{ display: "inline", paddingLeft: '10px' }}>  BCVHGU1278391734</h5>
              </div>
              <div className={classes.typo} >
                <h4 className={classes.title}>Carbon Used :  </h4>
                <h5 style={{ display: "inline" , paddingLeft: '10px' }}>  Premium Quality</h5>
              </div>
              <div className={classes.typo} >
                <h4 className={classes.title} style={{paddingRight : '20px'}}>Tire Size :  </h4>
                {/* <h5 style={{ display: "inline" , paddingLeft: '10px' }}>  21 Inches</h5> */}

              </div>

              <div className={classes.typo} >
                <h4 className={classes.title}>Axis Size : </h4>
                <h5 style={{ display: "inline" , paddingLeft: '10px' }}>  12 inches</h5>
              </div>

              <div className={classes.typo} >
                <h4 className={classes.title}>Axis Size : </h4>
                {/* <Success style={{fontSize : '30px' }}>13 Inches</Success> */}
                <Button style={{ display: "inline" , marginLeft: '10px' }} color="success">13 Inches</Button>
              </div>

              <h4 className={classes.title}>Axis Alignment :</h4>
              <Button style={{ display: "inline" , marginLeft: '10px' }} color="danger">2 Inches</Button>

            </ul>
          </li>
        ))}
      </List>
    );
  }
}







export default withStyles(tablePageStyle)(TableStats);
