import React from 'react';
import { withStyles } from '@material-ui/core/styles';
import List from '@material-ui/core/List';
import Button from "components/CustomButtons/Button.jsx";
import axios from 'axios';

//css file
import tablePageStyle from "assets/jss/material-kit-react/views/tablePage.jsx";

class TableStats extends React.Component {

  state = {
    list: [
      {
        title: 'Tyre Model',
        description: ' BCVHGU1278391734',
        type: 'text'
      },
      {
        title: 'Carbon Used',
        description: '  Premium Quality',
        type: 'text'
      },
      {
        title: 'Tire Size  ',
        description: ' 20 inches',
        type: 'text'
      },
      {
        title: 'Axis Size : ',
        description: '13 Inches',
        suitable: 'success',
        type: 'compare',
      },
      {
        title: 'Axis Alignment : ',
        description: '2 Inches',
        suitable: 'danger',
        type: 'compare'
      }
    ]
  }

  componentDidMount() {
    const self = this;
    axios.get('http://bitsotg.com:7878/data.json')
      .then(data => {
        console.log(data.data.tyres);

        self.setState({
          list : data.data.tyres
        })

       // console.log("This is the new array --> ", arr);

        // data.data.sort(function(a, b) {
        //   return b.networth - a.networth;
        // });

        // self.setState({
        //   list: data.data
        // });
      })
      .catch(err => {
        console.log('ERROR AA RHA HAI BHAI')
        console.log(err);
      });
  }


  render() {
    const { classes} = this.props;

    return (
      <List className={classes.root} subheader={<li />}>
        {[0].map(sectionId => (
          <li key={`section-${sectionId}`} className={classes.listSection}>
            <ul className={classes.ul}>

              {
                this.state.list.map((el, index) => {

                  if (el.description === 'present') {
                    return (
                      <div className={classes.typo} >
                      <h4 className={classes.title}>{el.title} </h4>
                      <Button style={{ display: "inline", marginLeft: '10px' }} color="danger">Flaw Present</Button>
                    </div>
                    );
                  } else if (el.description === null) {
                    return(
                      <div className={classes.typo} >
                      <h4 className={classes.title}>{el.title} </h4>
                      <Button style={{ display: "inline", marginLeft: '10px' }} color="success">Flaw Not Present</Button>
                    </div>
                    );                  
                  }
                })
              }

            </ul>
          </li>
        ))}
      </List>
    );
  }
}







export default withStyles(tablePageStyle)(TableStats);
